import React, { useState , useEffect } from 'react'

export const DateTime = () => {

    var [date, setDate] = useState(new Date());

    useEffect(() => {
        var timer = setInterval(() =>setDate(newDate()), 1000)
        return function cleanup() {
            clearInterval(timer)
        }
    });

    return(
        <div>
            <p> Time: {date.toLocaleTimeString()} </p>
        </div>
    )
}
